    static getAllCards() {
        return this.cards;
    }
    static getCardInfo(name) {
        for(const card of CardInformation.cards) {
            if(card["name"] == name) {
                return card;
            }
        }
        return undefined;
    }
    // Given a path to a card, translate it to the name
    static pathToName(path:string) {
        let card_name = path.substring(path.lastIndexOf("/") + 1).replace("Card.png", "");
        if(card_name == "X-Bow") {
            return "X-Bow";
        } else if(card_name.indexOf("PEKKA") != -1) {
            if(card_name.indexOf("Mini") != -1) {
                return "Mini P.E.K.K.A";
            } else {
                return "P.E.K.K.A";
            }
        } else {
            let temp = card_name.substring(0, 1);
            for(let i = 1;i < card_name.length;i++) {
                if(card_name.charAt(i) == card_name.charAt(i).toUpperCase()) {
                    temp += " ";
                }
                temp += card_name.charAt(i);
            }
            return temp;
        }

    }
    // Given a array of card names, return a string with the link of cards appended
    static getDeckLink(cards:string[]) {
        let url = "https://link.clashroyale.com/deck/en?deck="; // Card ids are separated by colons
        const cardSet = new Set(CardInformation.cards);
        for(const card of cards) {
            url += CardInformation.cardsMap[card].card_id + ";";
        }
        return url.substring(0, url.length - 1);
    }
